        String topic = message.getText();
        String data = message.getText();
        System.out.println("Received message from " + session.getId() + ": " + data);

        if (topic.equals("my-topic")) {
            // Handle the message
            System.out.println("Received message from " + session.getId() + ": " + data);
        } else {
            // Handle unknown topic
            System.out.println("Unknown topic: " + topic);
        }
    }

    @Override
    public void handleDisconnect(WebSocketSession session, Throwable exception) {
        sessions.remove(session.getId());
    }
