// Get the URL bar
const urlBar = document.querySelector('body > #urlBar');

// Add an event listener to the URL bar
urlBar.addEventListener('input', function() {
  // Get the input value
  const inputValue = this.value;

  // Use a regular expression to match Tezos addresses
  const regex = /^[0-9a-f]{40,42}$/;
  if (regex.test(inputValue)) {
    // Get the Tezos address
    const address = inputValue;

    // Use the `fetch` API to make a GET request to tzkt.io
    fetch(`https://tzkt.io/${address}`)
     .then(response => response.text())
     .then(text => {
        // Update the URL bar with the linked address
        this.value = text;
      })
     .catch(error => {
        console.error(error);
      });
  }
});
