import 'package:flutter/services.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  static const platformChannel = MethodChannel('com.example.myapp/native');

  @override
  Widget build(BuildContext context) {
    // Use the platform channel to call native functions
    return MaterialApp(
      home: Scaffold(
        appBar: AppBar(
          title: Text('Native Integration Example'),
        ),
        body: Center(
          child: ElevatedButton(
            onPressed: () {
              platformChannel.invokeMethod('nativeFunction');
            },
            child: Text('Call Native Function'),
          ),
        ),
      ),
    );
  }

  Future<void> callNativeFunction() async {
    try {
      final result = await platformChannel.invokeMethod('nativeFunction');
      print(result);
    } catch (e) {
      print('Failed to invoke method: $e');
    }
  }
}
```

