class NativeFlutterPlugin: FlutterPlugin, MethodCallHandler {
    private lateinit var channel: MethodChannel

    override fun onAttachedToEngine(@NonNull flutterEngine: FlutterEngine) {
        channel = MethodChannel(flutterEngine.dartExecutor.binaryMessenger, "com.example.myapp/native")
    }

    override fun onMethodCall(@NonNull methodCall: MethodCall, @NonNull result: MethodChannel.Result) {
        if (methodCall.method == "nativeFunction") {
            performNativeFunction(methodCall, result)
        } else if (methodCall.method == "getNativeVersion") {
            val version = "1.0" // Replace with your native version
            result.success(version)
        }
    }

    private fun performNativeFunction(methodCall: MethodCall, result: MethodChannel.Result) {
        try {
            val nativeResult = nativeFunction()
            result.success(nativeResult)
        } catch (e: Throwable) {
            result.error("ERROR", "Native function failed", e)
        }
    }

    private fun nativeFunction(): String {
        return "Native function called successfully"
    }
