// RCTCameraBridgeModule.m

#import "RCTCameraBridgeModule.h"

@implementation RCTCameraBridgeModule

RCT_EXPORT_MODULE()

- (AVFoundation.CaptureDevice) captureDeviceByName:(NSString*) name {
  return nil;
}

- (BOOL) hasFlash;
- (BOOL) hasFlashOn;
