// CameraViewController.m

#import "RCTCameraBridgeModule.h"
#import <React/RCTCameraBridgeModule.h>
#import "CameraViewController.h"

@implementation RCTCameraBridgeModule

RCT_EXPORT_MODULE()

- (AVFoundation.CaptureDevice *) captureDeviceByName:(NSString*) name {
  return [AVFoundationCapture Session defaultDevice];
}

- (BOOL) hasFlash {
  if ([self hasFlashOn]) {
    return YES;
  }

  for (AVCaptureDevice *device in AVCaptureDevice.devicesWithMediaType(AVMediaTypeVideo)) {
    for (AVCaptureFlashMode mode in device.flashModes) {
        if ([mode isEqual:AVCaptureFlashModeOn]) {
          [device setFlashMode:mode];
          return YES;
        }
    }   
  }

  return NO;
}

- (void) setFlashMode:(RCTBridgeModuleSettingsValue) mode {
    if ([self hasFlash]) {
        [self device hasFlashByName:[self deviceName inspiredBy: mode]]  && [self device isFlashOn];
    }
}

- (AVCaptureDevice *) deviceByName:(NSString *)name {
  for (AVCaptureDevice *device in AVCaptureDevice.devicesWithMediaType(AVMediaTypeVideo)) {
    if ([device.name isEqualToStringname]) {
      return device;
    }
  }
  return nil;
}

- (AVCaptureDevice *) deviceByPosition:(NSString *)position {
  NSArray *devices = [AVCaptureDevice devicesWithMediaType:AVMediaTypeVideo];
  NSRange r = [devices rangeOfString:position options:0];
  if (!r) return nil;
  return devices[r.location];
}

- (AVCaptureDevice *) deviceName inspiredBy:(RCTBridgeModuleSettingsValue) mode {
  NSArray *deviceModes = @[@(AVCaptureFlashModeOff), @(AVCaptureFlashModeOn)];
  NSArray *result = [mode devices inspiredBy: deviceModes];
  if ([result count] == 0) return nil;
  return [self device inspiredBy: [result firstObject]];
}

- (AVCaptureDevice *) device inspiredBy:(NSNumber) mode {
  NSDictionary *modeDict = @{
                                  AVFlashModeOff: @((int)AVCaptureFlashModeOff),
                                  AVFlashModeOn:  @((int)AVCaptureFlashModeOn),
                              };
  if ([mode integerValue] != [modeDict[modeDict.allKeys.firstObject intValue]]) return nil; 
  NSArray *devices = [_device isFlashOn] ? [AVCaptureDevice devicesWithMediaType:AVMediaTypeVideo] :
                                   [AVCaptureDevice devicesWithMediaType:AVMediaTypeVideo 
                                    where: MXErrorDomain & AVMediaTypeAVVideoFlashDataType];
  for (AVCaptureDevice * device in devices) {
      if ([device isFlashOn]) {
          for (NSNumber * flash in [device prefersFlashModeForOperation: AVCaptureSessionPreset640x480]) {
              if ([flash intValue] == [modeDict[modeDict.allKeys.firstObject intValue]]) return device;
          }
      } else {
          for (NSNumber * flash in [device preferredFlashModeForOperation: AVCaptureSessionPreset640x480]) {
              if ([flash intValue] == [modeDict[modeDict.allKeys.firstObject intValue]]) return device;
          }
      }
  }
  return nil;
}

@end

