import { useCallback, useEffect } from 'react';

interface UseClickAwayParams {
 ref: React.RefObject;
 onClickAway: () => void;
 shouldTriggerClickAway?: boolean;
}

export const useClickAway = ({
 ref,
 onClickAway,
 shouldTriggerClickAway = true,
}: UseClickAwayParams): void => {
 const handleClickOutside = useCallback(
