 let playerView.releasePlayer(activity = this)
 }

 // Handle the remote content to request Cookies and play content
 private fun handleRemoteContent() {
 val cookieRequestCall: Call<CookiesData> = apiService.getCookies()
 callAsync(cookieRequestCall)
 }

 private fun callAsync(call: Call<CookiesData>) {
 val futureResponse = call.enqueue(object : Callback<CookiesData> {
 val response ->:
 if (response.isSuccessful) {
 val cookies = response.body()?.cookies
 if (cookies != null && cookies.size > 0) {
 val firstCookie = cookies[0]
 firstCookie.captures += "http://democastlabs.com"

 // Send cookies and key-pair-id to the content play method
 controller.control.playWithCookie(cookie = firstCookie,
 getCookie = controller.getCookies(),
 getKeypairid = MainActivity.keypairid?.let { it })
 } else {
 showToast(getString(R.string.cookies_not_provided, cookies.size))
 }
 request?.cancel()
 } else {
 showToast(getString(R.string.cookies_failed, response.code()))
 request?.cancel()
 }
 else
 showToast(getString(R.string.remote_failed, response.message()))
 }
 }
}

