            if (timer == null)
            {
                timer = new System.Timers.Timer(100);
                timer.Elapsed += OnTimerElapsed;
                timer.Start();
            }

            isRunning = true;
            UpdateTimeDisplay();
        }

        private void StopStopwatch()
