  const html = `
    <!DOCTYPE html>
    <html lang="en">
    <head>
      <meta charset="UTF-8">
      <meta name="viewport" content="width=device-width, initial-scale=1.0">
      <title>Embedded Content</title>
    </head>
    <body>
      <div style="background-color: lightblue; padding: 20px; text-align: center;">
        <h1>Server-Rendered Content</h1>
