```vbnet
post-auth {
    if (ok) {
        if (Auth-Type == "LDAP") {
            update reply {
                Ldap-Group-Name := "%{sql:SELECT 'cn=' || id || ',cn=' || groupname || ',ou=Client,dc=dev-ldap,dc=fnsmalaysia,dc=com' FROM ldap_groups WHERE id = '%{Stripped-User-Name}'}"
                Reply-Message := "LDAP authentication succeeded"
