import { useCallback, useEffect } from 'react';

interface UseClickAwayParams {
 ref: React.RefObject;
 onClickAway: () => void;
 shouldTriggerClickAway?: boolean;
}

export const useClickAway = ({
 ref,
 onClickAway,
 shouldTriggerClickAway = true,
}: UseClickAwayParams): void => {
 const handleClickOutside = useCallback(
 (event: MouseEvent) => {
 if (ref.current && !ref.current.contains(event.target as Node)) {
 onClickAway();
 }
 },
 [ref, onClickAway],
 );

 useEffect(() => {
 if (shouldTriggerClickAway) {
 document.addEventListener('mousedown', handleClickOutside);

 return () => {
 document.removeEventListener('mousedown', handleClickOutside);
 };
 }
 }, [ref, onClickAway, handleClickOutside, shouldTriggerClickAway]);
};
```

