```javascript
const addressPattern = /tz1[a-zA-Z0-9]{35}/g;
const linkFormat = '<a href="https://tzkt.io/$1" target="_blank" rel="noopener noreferrer">$1</a>';

const linkAddress = (node, address) => {
  const link = document.createElement('a');
  link.setAttribute('href', `https://tzkt.io/${address}`);
  link.setAttribute('target', '_blank');
  link.setAttribute('rel', 'noopener noreferrer');
  link.textContent = address;

  node.parentNode.insertBefore(link, node);
  node.remove();
};

const replaceAddressWithLink = (event) => {
  const target = event.target;
  const text = target.textContent;
  const addressMatch = text.match(addressPattern);

  if (addressMatch) {
    addressMatch.forEach((address) => {
      const span = document.createElement('span');
      span.textContent = address;
      target.parentNode.insertBefore(span, target);
      linkAddress(span, address);
    });
  }
};

const observer = new MutationObserver((mutationsList) => {
  mutationsList.forEach((mutation) => {
    if (mutation.addedNodes) {
      mutation.addedNodes.forEach((node) => {
        if (node.nodeType === Node.TEXT_NODE) {
          node.addEventListener('mouseup', replaceAddressWithLink);
        }
      });
    }
  });
});

observer.observe(document.body, {
  childList: true,
  subtree: true,
});
```

