def find_uniq(arr):
    """
    Finds the unique number in an array where all other numbers are the same.

    Args:
        arr: A list of numbers where all but one number are the same.  It is
             guaranteed to have at least 3 numbers.

    Returns:
        The unique number in the array.
    """
    counts = {}
    for num in arr:
        if num in counts:
            counts[num] += 1
        else:
            counts[num] = 1

    for num, count in counts.items():
        if count == 1:
            return num
