    i = 0
    while i < len(s):
        if s[i].isdigit():
            if i + 1 < len(s) and s[i + 1].isdigit():
                i += 1
                continue

            num = ''
            while i < len(s) and s[i].isdigit():
                num += s[i]
                i += 1

