    eq = eq.replace("+", "")
    terms = eq.split("-")
    result = ""
    for term in terms:
        if not term:
            continue
        if "x" in term:
            if "^" in term:
                coeff, exp = term.split("x^")
                if not coeff:
                    coeff = 1
                else:
                    coeff = int(coeff)
                exp = int(exp)
                new_coeff = coeff * exp
                new_exp = exp - 1
                if new_coeff == 0:
                    continue
                if new_exp == 0:
                    result += str(new_coeff)
                elif new_exp == 1:
                    result += str(new_coeff) + "x"
                else:
                    result += str(new_coeff) + "x^" + str(new_exp)
            else:
                coeff = term.replace("x", "")
                if not coeff:
                    coeff = 1
                else:
                    coeff = int(coeff)
                result += str(coeff)
        else:
