def outcome(n, s, k):
    """
    Calculates the number of outcomes when rolling n dice with s sides each, 
    that add up to a specified number k.

    Args:
        n (int): The number of dice.
        s (int): The number of sides on each die.
        k (int): The target sum.

    Returns:
        int: The number of outcomes that add up to k.
    """

    if k < n or k > n * s:
        return 0

    dp = {}

    def solve(dice_left, target_sum):
        if dice_left == 0:
            return 1 if target_sum == 0 else 0

        if (dice_left, target_sum) in dp:
            return dp[(dice_left, target_sum)]

        count = 0
        for face in range(1, s + 1):
            if target_sum - face >= 0:
                count += solve(dice_left - 1, target_sum - face)

        dp[(dice_left, target_sum)] = count
        return count

    return solve(n, k)
