def count_odd_pentaFib(n):
	"""
	Calculates the number of distinct odd values in the pentaFibonacci sequence up to the n-th term.

	Args:
		n: The number of terms in the sequence to consider (inclusive).

	Returns:
		The number of distinct odd values in the sequence.
	"""

	if n <= 0:
		return 0

	sequence = [0] * (n + 1)
	sequence[0] = 0
	if n >= 1:
		sequence[1] = 1
	if n >= 2:
		sequence[2] = 1
	if n >= 3:
		sequence[3] = 2
	if n >= 4:
		sequence[4] = 4

	for i in range(5, n + 1):
		sequence[i] = sequence[i-1] + sequence[i-2] + sequence[i-3] + sequence[i-4] + sequence[i-5]

	odd_values = set()
	for i in range(1, n + 1, 2):
		if sequence[i] % 2 != 0:
			odd_values.add(sequence[i])

	return len(odd_values)
