def prime_bef_aft(num):
	"""
	Given a number n, find the largest prime below n and the smallest prime above n.
	Return the result in a list like [befPrime, aftPrime].
	"""
	def is_prime(n):
		if n <= 1:
			return False
		for i in range(2, int(n**0.5) + 1):
			if n % i == 0:
				return False
		return True

	bef_prime = num - 1
	while not is_prime(bef_prime):
		bef_prime -= 1

	aft_prime = num + 1
	while not is_prime(aft_prime):
		aft_prime += 1

	return [bef_prime, aft_prime]
