class Solution:
    def reorderedPowerOf2(self, N: int) -> bool:

        def get_digit_counts(n):
            counts = [0] * 10
            while n > 0:
                counts[n % 10] += 1
                n //= 10
            return tuple(counts)

        n_counts = get_digit_counts(N)

        for i in range(31):  # 2^30 is the largest power of 2 within the range
            power_of_2 = 2**i
            power_counts = get_digit_counts(power_of_2)
            if n_counts == power_counts:
                return True

        return False
