def merge_arrays(a, b):
    """
    Merges two sorted arrays to form a new array of unique items.

    If an item is present in both arrays, it should be part of the
    resulting array if and only if it appears in both arrays the same
    number of times.

    Args:
      a: A sorted array.
      b: A sorted array.

    Returns:
      The resulting sorted array.
    """

    merged = []
    i = 0
    j = 0
    counts_a = {}
    counts_b = {}

    for x in a:
        counts_a[x] = counts_a.get(x, 0) + 1
    for x in b:
        counts_b[x] = counts_b.get(x, 0) + 1

    while i < len(a) and j < len(b):
        if a[i] < b[j]:
            merged.append(a[i])
            i += 1
        elif a[i] > b[j]:
            merged.append(b[j])
            j += 1
        else:
            if counts_a[a[i]] == counts_b[b[j]]:
                merged.append(a[i])
                i += 1
                j += 1
            else:
                i += 1
                j += 1

    while i < len(a):
        merged.append(a[i])
        i += 1

    while j < len(b):
        merged.append(b[j])
        j += 1

    result = []
    seen = set()
    for x in merged:
        if x not in seen:
            result.append(x)
            seen.add(x)

    final_result = []
    for num in result:
      if (num in counts_a and num in counts_b) and counts_a[num] == counts_b[num]:
        final_result.append(num)
      elif num not in counts_b:
        final_result.append(num)
      elif num not in counts_a:
        final_result.append(num)

    return final_result
