def calc(cards):
    n = len(cards)
    dp = {}

    def solve(left, right, turn):
        if (left, right, turn) in dp:
            return dp[(left, right, turn)]

        if left > right:
            return 0

        # Take from the left
        take_left = cards[left] * (2**turn) + solve(left + 1, right, turn + 1)

        # Take from the right
        take_right = cards[right] * (2**turn) + solve(left, right - 1, turn + 1)

        dp[(left, right, turn)] = max(take_left, take_right)
        return dp[(left, right, turn)]

    return solve(0, n - 1, 0)
