class Solution:
    def largestIsland(self, grid: List[List[int]]) -> int:
        n = len(grid)
        visited = [[False] * n for _ in range(n)]
        island_id = 2
        island_sizes = {}

        def dfs(row, col):
            if row < 0 or row >= n or col < 0 or col >= n or visited[row][col] or grid[row][col] == 0:
                return 0
            visited[row][col] = True
            size = 1
            size += dfs(row + 1, col)
            size += dfs(row - 1, col)
            size += dfs(row, col + 1)
            size += dfs(row, col - 1)
            return size

        # Find all islands and their sizes
        for i in range(n):
            for j in range(n):
                if grid[i][j] == 1 and not visited[i][j]:
                    size = dfs(i, j)
                    island_sizes[island_id] = size
                    # Mark the island with a unique id
                    for r in range(n):
                        for c in range(n):
                            if visited[r][c] and grid[r][c] == 1:
                                grid[r][c] = island_id
                    island_id += 1

        # If there are no 0s, return the size of the existing island
        if len(island_sizes) == 0:
            return n * n

        max_island_size = 0
        if len(island_sizes) > 0:
            max_island_size = max(island_sizes.values())

        # Try changing each 0 to 1 and calculate the island size
        for i in range(n):
            for j in range(n):
                if grid[i][j] == 0:
                    neighbors = set()
                    if i > 0 and grid[i - 1][j] != 0:
                        neighbors.add(grid[i - 1][j])
                    if i < n - 1 and grid[i + 1][j] != 0:
                        neighbors.add(grid[i + 1][j])
                    if j > 0 and grid[i][j - 1] != 0:
                        neighbors.add(grid[i][j - 1])
                    if j < n - 1 and grid[i][j + 1] != 0:
                        neighbors.add(grid[i][j + 1])

                    new_island_size = 1
                    for neighbor in neighbors:
                        new_island_size += island_sizes.get(neighbor, 0)
                    max_island_size = max(max_island_size, new_island_size)

        return max_island_size
