	if not isinstance(enum_list, list):
		return False

	for item in enum_list:
		if not isinstance(item, tuple) or len(item) != 2:
			return False

		index, char = item
		if not isinstance(index, int) or not isinstance(char, str) or len(char) != 1 or not char.isalnum():
			return False

	if len(enum_list) != max(index for index, _ in enum_list) + 1:
		return False

	reconstructed_string = [''] * len(enum_list)
	for index, char in enum_list:
		reconstructed_string[index] = char

	return ''.join(reconstructed_string)
