from typing import List

class Solution:
    def optimalDivision(self, nums: List[int]) -> str:
        if not nums:
            return ""

        def calculate(expression: str) -> float:
            try:
                return eval(expression)
            except (ZeroDivisionError, TypeError):
                return float('-inf')

        def generate_expressions(index: int, current_expression: str) -> List[str]:
            if index == len(nums):
                return [current_expression]

            expressions = []

            # Option 1: Divide with the current number
            if index == 0:
