class Solution:
    def mergeStones(self, stones: List[int], K: int) -> int:
        n = len(stones)
        if (n - 1) % (K - 1) != 0:
            return -1

        dp = {}

        def calculate_cost(left, right):
            if (left, right) in dp:
                return dp[(left, right)]

            if left == right:
                return 0

            min_cost = float('inf')
            for i in range(left, right, K - 1):
                min_cost = min(min_cost, calculate_cost(left, i) + calculate_cost(i + 1, right))

            if (right - left) % (K - 1) == 0:
                total_stones = sum(stones[left:right+1])
                min_cost += total_stones

            dp[(left, right)] = min_cost
            return min_cost

        return calculate_cost(0, n - 1)
