class Solution:
    def minRemoveToMakeValid(self, s: str) -> str:
        stack = []
        indices_to_remove = set()

        for i, char in enumerate(s):
            if char == '(':
                stack.append(i)
            elif char == ')':
                if stack:
                    stack.pop()
                else:
                    indices_to_remove.add(i)

        while stack:
            indices_to_remove.add(stack.pop())

        result = ""
        for i, char in enumerate(s):
            if i not in indices_to_remove:
                result += char

        return result
