class Solution:
    def pyramidTransition(self, bottom: str, allowed: List[str]) -> bool:
        n = len(bottom)
        if n == 0:
            return True

        def check(level):
            if len(level) == 1:
                return True

            next_level = ""
            for i in range(len(level) - 1):
                a = level[i]
                b = level[i+1]
                found = False
                for triple in allowed:
                    if triple[0] == a and triple[1] == b:
                        next_level += triple[2]
                        found = True
                        break
                if not found:
                    return False
            return check(next_level)

        return check(bottom)
