def summationOfPrimes(primes):
	"""
	Calculates the sum of all prime numbers below or equal to a given number.

	Args:
	primes (int): The upper limit for finding prime numbers.

	Returns:
	int: The sum of all prime numbers below or equal to the input number.
	"""

	def is_prime(n):
		if n <= 1:
			return False
		if n <= 3:
			return True
		if n % 2 == 0 or n % 3 == 0:
			return False
		i = 5
		while i * i <= n:
			if n % i == 0 or n % (i + 2) == 0:
				return False
			i += 6
		return True

	sum_of_primes = 0
	for i in range(2, primes + 1):
		if is_prime(i):
			sum_of_primes += i
	return sum_of_primes
