class Solution:
    def longestDupSubstring(self, S: str) -> str:
        n = len(S)
        longest_dup = ""

        for length in range(1, n):
            seen = set()
            dup_found = False
            for i in range(n - length + 1):
                substring = S[i:i+length]
                if substring in seen:
                    longest_dup = substring
                    dup_found = True
                    break
                else:
                    seen.add(substring)
            if not dup_found:
                break

        return longest_dup
