def is_haiku(text):
    """
    Checks if the supplied text is a haiku or not.
    """
    lines = text.splitlines()
    if len(lines) != 3:
        return False

    def count_syllables(word):
        vowels = "aeiouy"
        count = 0
        prev_was_vowel = False
        for char in word.lower():
            if char in vowels:
                if not prev_was_vowel:
                    count += 1
                prev_was_vowel = True
            else:
                prev_was_vowel = False

        if len(word) > 1 and word.endswith("e") and word[-2] not in vowels:
            count -= 1

        return max(1, count)  # Ensure at least one syllable

    syllable_counts = []
    for line in lines:
        words = line.split()
        total_syllables = 0
        for word in words:
            total_syllables += count_syllables(word)
        syllable_counts.append(total_syllables)

    return syllable_counts == [5, 7, 5]
