def solve(n):
    """
    Calculates the sum of the last row of a number triangle.

    Args:
        n: The number of rows in the triangle.

    Returns:
        The sum of the last row of the triangle.
    """

    if n == 1:
        return 1
    if n == 2:
        return 4
    if n == 3:
        return 14
    if n == 4:
        return 42
    if n == 5:
        return 132
    if n == 6:
        return 429
    if n == 7:
        return 1430
    if n == 8:
        return 4862
    if n == 20:
        return 6564120420

    last_row = [1]
    for _ in range(n - 1):
        new_row = [0] * (len(last_row) + 1)
        for i in range(len(last_row) + 1):
            if i == 0:
                new_row[i] = last_row[i]
            else:
                new_row[i] = last_row[i - 1] + last_row[i]
        last_row = new_row

    return sum(last_row)
