from fractions import Fraction

def bernoulli_number(n):
    """
    Calculates the n-th Bernoulli number.

    Args:
        n: A non-negative integer.

    Returns:
        The n-th Bernoulli number as a Fraction.
    """

    if n == 0:
        return Fraction(1)

    B = [Fraction(0)] * (n + 1)
    B[0] = Fraction(1)

    for m in range(1, n + 1):
        B[m] = Fraction(0)
        for k in range(m):
            B[m] -= B[k] * comb(m + 1, k)
        B[m] /= m + 1

    if n == 1:
        return B[1]
    elif n == 2:
        return B[2]
    elif n == 6:
        return B[6]
    elif n == 42:
        return B[42]
    elif n == 22:
        return B[22]
    else:
        return B[n]

def comb(n, k):
    """
    Calculates the binomial coefficient "n choose k".

    Args:
        n: The total number of items.
        k: The number of items to choose.

    Returns:
        The binomial coefficient.
    """
    if k < 0 or k > n:
        return 0
    if k == 0 or k == n:
        return 1
    if k > n // 2:
        k = n - k
    result = 1
    for i in range(k):
        result = result * (n - i) // (i + 1)
    return result
