def camelize(str_):
	"""
	Converts a string from any format into CamelCase.
	Supports symbols too.
	"""
	components = str_.split()
	if len(components) == 0:
		return ""

	result = components[0]
	for component in components[1:]:
		result += component.capitalize()

	parts = []
	current_word = ""
	for char in result:
		if char.isalpha():
			current_word += char
		else:
			if current_word:
				parts.append(current_word)
				current_word = ""

	if current_word:
		parts.append(current_word)

	if not parts:
		return result

	final_result = parts[0]
	for part in parts[1:]:
		final_result += part.capitalize()

	return final_result
