class Solution:
    def uniquePathsIII(self, grid: List[List[int]]) -> int:
        """
        Finds the number of 4-directional walks from the starting square to the ending square,
        that walk over every non-obstacle square exactly once.

        Args:
            grid: A 2-dimensional grid representing the grid.

        Returns:
            The number of 4-directional walks.
        """
        rows, cols = len(grid), len(grid[0])
        start_row, start_col = -1, -1
        end_row, end_col = -1, -1
        empty_squares = 0

        for r in range(rows):
            for c in range(cols):
                if grid[r][c] == 1:
                    start_row, start_col = r, c
                elif grid[r][c] == 2:
                    end_row, end_col = r, c
                elif grid[r][c] == 0:
                    empty_squares += 1

        def is_valid(row, col):
            return 0 <= row < rows and 0 <= col < cols and grid[row][col] != -1

        def solve(row, col, visited):
            if row == end_row and col == end_col:
                if len(visited) == empty_squares + 1:  # +1 for the start square
                    return 1
                else:
                    return 0

            count = 0
            directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
            for dr, dc in directions:
                new_row, new_col = row + dr, col + dc
                if is_valid(new_row, new_col) and (new_row, new_col) not in visited:
                    new_visited = visited.copy()
                    new_visited.add((new_row, new_col))
                    count += solve(new_row, new_col, new_visited)

            return count

        return solve(start_row, start_col, {(start_row, start_col)})
