class Solution:
    def uniquePathsIII(self, grid: List[List[int]]) -> int:
        rows, cols = len(grid), len(grid[0])
        start_row, start_col = -1, -1
        empty_squares = 0

        for r in range(rows):
            for c in range(cols):
                if grid[r][c] == 1:
                    start_row, start_col = r, c
                if grid[r][c] != -1:
                    empty_squares += 1

        def solve(row, col, visited, count):
            if row < 0 or row >= rows or col < 0 or col >= cols or grid[row][col] == -1 or (row, col) in visited:
                return 0

            visited.add((row, col))

            if grid[row][col] == 2:
                if len(visited) == empty_squares:
                    visited.remove((row, col))
                    return 1
                else:
                    visited.remove((row, col))
                    return 0

            paths = 0
            paths += solve(row + 1, col, visited, count + 1)
            paths += solve(row - 1, col, visited, count + 1)
            paths += solve(row, col + 1, visited, count + 1)
            paths += solve(row, col - 1, visited, count + 1)

            visited.remove((row, col))
            return paths

        return solve(start_row, start_col, set(), 0)
