            # Check for draw
            if len(moves) == i + 1 and self.is_grid_full(grid):
                return "Draw"

        return "Pending"

    def check_winner(self, grid):
        # Check rows
        for row in grid:
            if row[0] != " " and row[0] == row[1] == row[2]:
                return True

        # Check columns
        for col in range(3):
            if grid[0][col] != " " and grid[0][col] == grid[1][col] == grid[2][col]:
