        # Check diagonals
        if grid[0][0] != " " and grid[0][0] == grid[1][1] == grid[2][2]:
            return True
        if grid[0][2] != " " and grid[0][2] == grid[1][1] == grid[2][0]:
            return True

        return False

    def is_grid_full(self, grid):
        for row in grid:
            for cell in row:
                if cell == " ":
                    return False
