    edges = []
    for _ in range(n - 1):
        u, v, w = map(int, input().split())
        edges.append((u, v, w))

    queries = []
    for _ in range(q):
        a, b = map(int, input().split())
        queries.append((a, b))

    def get_path(start, end):
        path = []
        visited = {start}
        queue = [(start, [start])]

        while queue:
            node, current_path = queue.pop(0)

            if node == end:
                return current_path

            for u, v, w in edges:
                neighbor = None
                if u == node and v not in visited:
                    neighbor = v
                elif v == node and u not in visited:
                    neighbor = u

                if neighbor:
                    visited.add(neighbor)
                    queue.append((neighbor, current_path + [neighbor]))

        return None

    def calculate_profit(path):
        profit = 0
        for i in range(len(path) - 1):
            u = path[i]
            v = path[i+1]
            for edge_u, edge_v, edge_w in edges:
                if (edge_u == u and edge_v == v) or (edge_u == v and edge_v == u):
                    profit += edge_w
                    break
        return profit

    results = []
    for a, b in queries:
        path = get_path(a, b)

        if path:
            max_profit = calculate_profit(path)

            # Consider reversing direction once


            for i in range(len(path)-1):

              reversed_path = path[:i+1] + path[i+1:][::-1]

              reversed_profit = calculate_profit(reversed_path)

              max_profit = max(max_profit, reversed_profit)


            results.append(max_profit)
        else:
            results.append(0)

