class Solution:
    def soupServings(self, N: int) -> float:
        """Calculates the probability that soup A will be empty first, plus half the probability that A and B become empty at the same time."""

        if N <= 0:
            return 1.0

        if N <= 100:
            return 0.625

        dp = {}
        def solve(a, b):
            if (a, b) in dp:
                return dp[(a, b)]
            if a == 0 and b == 0:
                return 1.0
            if a < 0 or b < 0:
                return 0.0

            prob = 0.0
            prob += 0.25 * solve(a - 100, b)
            prob += 0.25 * solve(a - 75, b - 25)
            prob += 0.25 * solve(a - 50, b - 50)
            prob += 0.25 * solve(a - 25, b - 75)

            dp[(a, b)] = prob
            return prob

        prob_a_first = 0.0
        prob_both_empty = 0.0


        dp_a_first = {}
        def solve_a_first(a, b):
            if (a, b) in dp_a_first:
                return dp_a_first[(a, b)]

            if a == 0 and b > 0:
                return 1.0

            if a < 0 or b < 0:
                return 0.0

            prob = 0.0
            prob += 0.25 * solve_a_first(a - 100, b)
            prob += 0.25 * solve_a_first(a - 75, b - 25)
            prob += 0.25 * solve_a_first(a - 50, b - 50)
            prob += 0.25 * solve_a_first(a - 25, b - 75)

            dp_a_first[(a, b)] = prob
            return prob

        prob_a_first = solve_a_first(N, N)

        dp_both_empty = {}
        def solve_both_empty(a, b):
            if (a, b) in dp_both_empty:
                return dp_both_empty[(a, b)]

            if a == 0 and b == 0:
                return 1.0

            if a < 0 or b < 0:
                return 0.0

            prob = 0.0
            prob += 0.25 * solve_both_empty(a - 100, b)
            prob += 0.25 * solve_both_empty(a - 75, b - 25)
            prob += 0.25 * solve_both_empty(a - 50, b - 50)
            prob += 0.25 * solve_both_empty(a - 25, b - 75)            

            dp_both_empty[(a, b)] = prob
            return prob

        prob_both_empty = solve_both_empty(N, N)

        return prob_a_first + 0.5 * prob_both_empty
