class Solution:
    def maxSatisfied(self, customers: List[int], grumpy: List[int], X: int) -> int:
        n = len(customers)
        satisfied = 0
        for i in range(n):
            if grumpy[i] == 0:
                satisfied += customers[i]

        max_extra = 0
        for i in range(n - X + 1):
            extra = 0
            for j in range(i, i + X):
