class Solution:
    def isSolvable(self, words: List[str], result: str) -> bool:
        chars = set("".join(words) + result)
        if len(chars) > 10:
            return False

        mapping = {}
        used_digits = set()

        def backtrack(index):
            if index == len(chars):
                if self.isValid(words, result, mapping):
                    return True
                else:
                    return False

            char = list(chars)[index]
            for digit in range(10):
                if digit not in used_digits:
                    mapping[char] = digit
                    used_digits.add(digit)

                    if backtrack(index + 1):
                        return True

                    used_digits.remove(digit)
                    del mapping[char]

            return False

        return backtrack(0)

    def isValid(self, words: List[str], result: str, mapping: dict) -> bool:
        word_values = []
        for word in words:
            num = 0
            for char in word:
                num = num * 10 + mapping[char]
            word_values.append(num)

        result_value = 0
        for char in result:
            result_value = result_value * 10 + mapping[char]

        if sum(word_values) == result_value:
            # Check for leading zeros
            for word in words:
                if len(word) > 1 and mapping[word[0]] == 0:
                    return False
            if len(result) > 1 and mapping[result[0]] == 0:
                return False
            return True
        else:
            return False
