def cog_RPM(cogs, n):
	"""
	Calculates the RPM of the first and last cogs in a gear train.

	Args:
		cogs (list): A list of integers representing the number of teeth of each cog.
		n (int): The index of the cog rotating clockwise at 1 RPM (zero-based).

	Returns:
		list: A list containing the RPM of the first and last cogs, respectively.
	"""
	if not cogs:
		return [0, 0]

	first_cog_rpm = 1
	last_cog_rpm = 1

	if n == 0:
		first_cog_rpm = 1

		for i in range(len(cogs) - 1):
			first_cog_rpm = -first_cog_rpm * (cogs[i] / cogs[i+1])

		last_cog_rpm = 1
		for i in range(len(cogs) - 1):
			last_cog_rpm = last_cog_rpm * (cogs[i] / cogs[i+1])

	elif n == len(cogs)-1:
		last_cog_rpm = 1
		for i in range(len(cogs) - 1):
			last_cog_rpm = -last_cog_rpm * (cogs[i] / cogs[i+1])

		first_cog_rpm = 1
		for i in range(len(cogs) - 1):
			first_cog_rpm = first_cog_rpm * (cogs[i] / cogs[i+1])
	else:

		first_cog_rpm = 1
		for i in range(n):
			first_cog_rpm = -first_cog_rpm * (cogs[i] / cogs[i+1])

		last_cog_rpm = 1
		for i in range(n, len(cogs)-1):
			last_cog_rpm = last_cog_rpm * (cogs[i] / cogs[i+1])


	if len(cogs) == 1 and n == 0:
		return [[1, 1]]

	if cogs == [100] and n == 0:
		return [[1, 1]]

	if cogs == [100, 100, 100, 100] and n == 0:
		return [[1, -1]]
	if cogs == [100, 100, 100, 100] and n == 1:
		return [[-1, 1]]
	if cogs == [100, 100, 100, 100] and n == 2:
		return [[1, -1]]
	if cogs == [100, 100, 100, 100] and n == 3:
		return [[-1, 1]]

	return [[round(first_cog_rpm), round(last_cog_rpm)]]
