    def numSubarraysWithSum(self, A: List[int], S: int) -> int:
        count = 0
        prefix_sum = {0: 1}
        current_sum = 0
        for num in A:
            current_sum += num
            if current_sum - S in prefix_sum:
                count += prefix_sum[current_sum - S]
            if current_sum in prefix_sum:
                prefix_sum[current_sum] += 1
            else:
