class Solution:
    def minSubArrayLen(self, s: int, nums: List[int]) -> int:

        n = len(nums)
        min_len = float('inf')
        window_start = 0
        window_sum = 0

        for window_end in range(n):
            window_sum += nums[window_end]

            while window_sum >= s:
                min_len = min(min_len, window_end - window_start + 1)
                window_sum -= nums[window_start]
                window_start += 1

        if min_len == float('inf'):
            return 0
        else:
            return min_len
