class Node:
    def __init__(self, data):
        self.data = data
        self.next = None

class LinkedList:
    def __init__(self):
        self.head = None

    def push(self, data):
        newNode = Node(data)
        newNode.next = self.head
        self.head = newNode

    def Length(self):
        """Counts the number of nodes in the linked list."""
        count = 0
        current = self.head
        while current:
            count += 1
            current = current.next
        return count

    def Count(self, data):
        """Counts the occurrences of an integer in the linked list."""
        count = 0
        current = self.head
        while current:
            if current.data == data:
                count += 1
            current = current.next
        return count

    def buildOneTwoThree(self):
        """Builds a linked list with values 1, 2, and 3."""
        self.push(3)
        self.push(2)
        self.push(1)
