def eval_object(v):
	"""
	Evaluates the given properties of an object using Switch/Cases.

	Args:
		v (list): A list of dictionaries, where each dictionary represents an object
		          with properties 'a', 'b', and 'operation'.

	Returns:
		list: A list of the evaluated results.
	"""
	results = []
	for obj in v:
		a = obj['a']
		b = obj['b']
		operation = obj['operation']

		result = 0
		switch = operation
		if switch == '+':
			result = a + b
		elif switch == '-':
			result = a - b
		elif switch == '*':
			result = a * b
		elif switch == '/':
			if b == 0:
				result = 0  # Handle division by zero
			else:
				result = int(a / b)  # Integer division
		elif switch == '%':
			if b == 0:
				result = 0  # Handle modulo by zero
			else:
				result = a % b
		elif switch == '**':
			result = a ** b

		results.append(result)
	return results
