class ListNode:
    def __init__(self, val=0, next=None):
        self.val = val
        self.next = next
class Solution:
    def nextLargerNodes(self, head: ListNode) -> List[int]:
        nodes = []
        curr = head
        while curr:
            nodes.append(curr.val)
            curr = curr.next

        n = len(nodes)
        answer = [0] * n
        stack = []

        for i in range(n):
            while stack and nodes[stack[-1]] < nodes[i]:
                answer[stack.pop()] = nodes[i]
            stack.append(i)

        return answer
