class Solution:
    def toHex(self, num: int) -> str:
        """
        Converts an integer to its hexadecimal representation.

        Args:
            num: The integer to convert.

        Returns:
            The hexadecimal representation of the integer as a string.
        """
        if num == 0:
            return "0"

        if num > 0:
            hex_digits = []
            while num > 0:
                remainder = num % 16
                if remainder < 10:
                    hex_digits.append(str(remainder))
                else:
                    hex_digits.append(chr(ord('a') + remainder - 10))
                num //= 16
            return "".join(hex_digits[::-1])
        else:
            # Two's complement for negative numbers
            num = (num & 0xFFFFFFFF)  # Ensure 32-bit representation
            hex_digits = []
            while num > 0:
                remainder = num % 16
                if remainder < 10:
                    hex_digits.append(str(remainder))
                else:
                    hex_digits.append(chr(ord('a') + remainder - 10))
                num //= 16
            return "".join(hex_digits[::-1])
