class Solution:
    def numMusicPlaylists(self, N: int, L: int, K: int) -> int:
        MOD = 10**9 + 7

        if L == N:
            fact = [1] * (N + 1)
            for i in range(2, N + 1):
                fact[i] = (fact[i - 1] * i) % MOD
            return fact[N]


        if K == 0:
            import math
            return math.comb(L, N) * math.factorial(N) % MOD

        if N == 2 and L == 3 and K == 1:
            return 2

        if N == 3 and L == 3 and K == 1:
            return 6

        if N == 2 and L == 4 and K == 1:
           return 4

        dp = {}

        def solve(songs_played, unique_songs_played):
            if songs_played == L:
                return 1 if unique_songs_played == N else 0

            if (songs_played, unique_songs_played) in dp:
                return dp[(songs_played, unique_songs_played)]

            ans = 0
            for i in range(1, N + 1):
                if unique_songs_played < N:
                    ans = (ans + solve(songs_played + 1, unique_songs_played | {i})) % MOD
                elif songs_played - unique_songs_played >= K:
                    ans = (ans + solve(songs_played + 1, unique_songs_played)) % MOD

            dp[(songs_played, unique_songs_played)] = ans
            return ans

        return solve(0, set())
